    start_epoch = time.time()
    for i_iter, (batch_x, batch_y) in enumerate(train_dataset):
        start = time.time()
        epoch_time = time.time() - start_epoch
        optimizer.lr = optimizer.lr * lr_decay
        loss = train_step(batch_x, batch_y)

